<template>
    <div class="slider">
    <img :src="images[currentIndex]" alt="Slide">
    <div class="controls">
      <button @click="prevSlide" :disabled="currentIndex === 0">Prev</button>
      <button @click="nextSlide" :disabled="currentIndex === images.length - 1">Next</button>
    </div>
  </div>
  </template>
  
  <script>

  
  export default {
    name: 'GalleryComponent',
    data() {
    return {
      images: [
        'data:image/jpeg;base64,',
        'https://altcraft.com/uploads/mem_s_kotami_fc9dd6ee5d.png',
        'https://avatars.dzeninfra.ru/get-zen_doc/4450356/pub_60be6a274adb2513a3616ee7_60be6bfe5c70d473835db959/scale_1200',
       
      ],
      currentIndex: 0
    };
  },
  methods: {
    prevSlide() {
      if (this.currentIndex > 0) {
        this.currentIndex--;
      }
    },
    nextSlide() {
      if (this.currentIndex < this.images.length - 1) {
        this.currentIndex++;
      }
    }
  }
};
    
  
  </script>
  
  <style scoped>
  
  .slider {
  position: relative;
}
.controls {
  position: absolute;
  bottom: 10px;
  left: 50%;
  transform: translateX(-50%);
}
  .content {
    padding: 20px;
  }
  </style>
  